const generator={init:function(){let a=document.getElementById("input"),b=this;document.getElementById("btn").addEventListener("click",function(){a.innerText=b.generateInsult();document.getElementById("input").style.display="block";document.getElementById("tablo").style.display="block";document.querySelector("section").style.display="none";document.getElementById("glas").style.display="none";document.getElementById("result").innerHTML="try again"})},generateInsult:function(){document.getElementById("input").style.display="none";document.getElementById("tablo").style.display="none";document.querySelector("section").style.display="block";document.getElementById("glas").style.display="block";let a=["it`s a beautiful day:\n","it`s a cloudy day:\n","it`s a rainy day:\n","it`s a cold day:\n"],b=["Since you can dress warmly for summer!\n","It`s cloudy outside, it`s better to wear a rug.\n","It`s raining outside, bring an umbrella!\n","It`s cold outside, dress warmer.\n"],c=["You can go into nature\n","You can go to the movies\n","You can go to a restaurant\n","You`d better not leave the house!\n"];return function(){return a[Math.floor(Math.random()*a.length)]+b[Math.floor(Math.random()*a.length)]+c[Math.floor(Math.random()*a.length)]}}()};generator.init();